templates.dax_template:
{{measure_name}} =
VAR _value = {{value_measure}}
VAR _maxValue = {{max_value}}
VAR _pct = MIN(MAX(_value / _maxValue, 0), 1)
VAR _size = {{size}}
VAR _strokeWidth = {{stroke_width}}
VAR _radius = (_size / 2) - (_strokeWidth / 2)
VAR _circumference = 2 * 3.14159 * _radius
VAR _dashArray = FORMAT(_pct * _circumference, "0.0") & " " & FORMAT(_circumference, "0.0")
VAR _center = _size / 2
VAR _svg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 " & _size & " " & _size & "'><circle cx='" & _center & "' cy='" & _center & "' r='" & _radius & "' fill='none' stroke='{{color_background}}' stroke-width='" & _strokeWidth & "'/><circle cx='" & _center & "' cy='" & _center & "' r='" & _radius & "' fill='none' stroke='{{color_fill}}' stroke-width='" & _strokeWidth & "' stroke-dasharray='" & _dashArray & "' transform='rotate(-90," & _center & "," & _center & ")' stroke-linecap='round'/></svg>"
RETURN "data:image/svg+xml;utf8," & _svg
