templates.dax_template:
{{measure_name}} =
VAR _value = {{value_measure}}
VAR _low = {{threshold_low}}
VAR _high = {{threshold_high}}
VAR _color =
    SWITCH(
        TRUE(),
        _value < _low, "{{color_bad}}",
        _value < _high, "{{color_warning}}",
        "{{color_good}}"
    )
VAR _svg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 100'><circle cx='50' cy='50' r='30' fill='" & _color & "'/></svg>"
RETURN "data:image/svg+xml;utf8," & _svg

